    @if (isRootPath) {
        <p>Whle einen Ordner aus der Liste aus</p>
    } @else if (empty) {
        <p>Hier gibt es noch nichts zu sehen</p>
    } @else {   
        @for (directory of (directories$ | async); track directory.name) {
            <content-list-directory [name]="directory.name" [size]="directory.size"></content-list-directory>
        }
        
        @for (file of (files$ | async); track file.name) {
            <content-list-file [name]="file.name" [size]="file.size"></content-list-file>
        }
    }
    @if (displayCreateDirectoryComponent) {
            <create-directory-component [indent]="false" (removeEmitter)="removeDirectoryCreateComponent()"></create-directory-component>
        }
} @else {
    <loading-spinner></loading-spinner>
}

