import { AsyncPipe } from '@angular/common';
import { Component, EventEmitter, HostBinding, Output } from '@angular/core';
import { Store } from '@ngxs/store';
import { Observable } from 'rxjs';
import { GetTreeSubDirectories, TreeViewStateName } from 'src/components/app/actions/tree.state';
import { CreateDirectoryComponent } from 'src/components/app/explorer/create-directory-component/CreateDirectoryComponent';
import { TreeViewDirectoryComponent } from 'src/components/app/explorer/tree-view/tree-view-directory/TreeViewDirectoryComponent';
import { LoadingSpinnerComponent } from 'src/components/app/loading-spinner/LoadingSpinnerComponent';

@Component({
	selector: 'tree-view',
	standalone: true,
	templateUrl: './TreeViewComponent.html',
	styleUrl: './TreeViewComponent.css',
	imports: [AsyncPipe, TreeViewDirectoryComponent, LoadingSpinnerComponent, CreateDirectoryComponent],
})
export class TreeViewComponent {
	private readonly store: Store;

	path: string = 'root';

	name!: string;

	@HostBinding('class.loaded')
	loaded: boolean = false;

	@Output()
	loadedEvent = new EventEmitter<void>();

	children$!: Observable<{ name: string; hasChildren: boolean }[]>;

	displayCreateDirectoryComponent: boolean = false;

	public constructor(store: Store) {
		this.store = store;
	}

	displayDirectoryCreateComponent() {
		this.displayCreateDirectoryComponent = true;
	}

	removeDirectoryCreateComponent() {
		this.displayCreateDirectoryComponent = false;
	}

	ngOnInit() {
		this.name = this.path.split('/').at(-1)!;

		this.children$ = this.store.select((state) => state[TreeViewStateName][this.path]?.children);

		this.children$.subscribe((content) => {
			if (content) {
				this.loaded = true;
				this.loadedEvent.emit();
			}
		});

		this.store.dispatch(new GetTreeSubDirectories(this.path));
	}
}

import { Observable } from 'rxjs';

import { AsyncPipe } from '@angular/common';
import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';
import { Select, Store } from '@ngxs/store';

import { PathState } from 'src/components/app/actions/path';
import { GetTreeSubDirectories, TreeViewStateName, TreeViewStateType } from 'src/components/app/actions/tree.state';
import { LoadingSpinnerComponent } from 'src/components/app/loading-spinner/LoadingSpinnerComponent';

@Component({
	selector: 'tree-view-directory',
	standalone: true,
	templateUrl: './TreeViewDirectoryComponent.html',
	styleUrl: './TreeViewDirectoryComponent.css',
	imports: [AsyncPipe, LoadingSpinnerComponent],
})
export class TreeViewDirectoryComponent {
	private readonly store: Store;

	private readonly router: Router;

	@Input({ required: true })
	path!: string;

	@Input({ required: true })
	hasChildren!: boolean;

	name!: string;

	collapsed: boolean = true;

	loaded: boolean = false;

	selected: boolean = false;

	children$!: Observable<{ name: string; hasChildren: boolean }[]>;

	@Select(PathState.path)
	appPath$!: Observable<string>;

	public constructor(store: Store, router: Router) {
		this.store = store;
		this.router = router;
	}

	ngOnInit() {
		this.name = this.path.split('/').at(-1)!;

		this.appPath$.subscribe((appPath: string) => {
			this.selected = appPath === this.path;
			this.collapsed = this.collapsed ? !new RegExp(`${this.path}(\/|$)`, 'im').test(appPath) : false;

			if (!this.collapsed) {
				this.store.dispatch(new GetTreeSubDirectories(this.path));
			}
		});

		this.children$ = this.store.select((state: TreeViewStateType) => {
			return state[TreeViewStateName][this.path]?.children;
		});

		this.children$.subscribe((content) => {
			if (content) {
				this.loaded = true;
			}
		});

		if (!this.hasChildren) {
			this.collapsed = true;
			this.loaded = true;
		}
	}

	onClick() {
		if (this.hasChildren && this.collapsed) {
			this.toggle();
		}

		this.router.navigate([this.path]);
	}

	toggle(e?: Event) {
		e?.stopPropagation();

		this.collapsed = !this.collapsed;

		if (!this.collapsed && !this.loaded) {
			this.store.dispatch(new GetTreeSubDirectories(this.path));
		}
	}
}

