			return;
		}

		return this.directoryService.getDirectoryContent(action.path).pipe(
			mergeMap((content) => {
				const observables = content.directories.map((directory) =>
					this.directoryService.getDirectoryMetadata((action.path.endsWith('/') ? action.path : action.path + '/') + directory.name).pipe(
						map((metadata) => ({
							id: ++this.id,
							name: metadata.name,
							path: metadata.path,
							isCollapsed: true,
							isSelected: false,
							hasChildren: metadata.directories > 0,
						}))
					)
				);

				return forkJoin(observables);
			}),
			tap((children) => {
				const tree = ctx.getState().tree;

				ctx.setState(
					patch({
						tree: {
							...tree,
							[PathUtils.ensureSlashes(action.path)]: children,
						},
					})
				);
			})
		);
	}

	@Action(DirectoryTreeActions.Remove)
	public remove(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Remove) {
		const { name, dir: parentPath } = PathUtils.parse(action.path);
		const { name: parentName, dir: grandparentPath } = PathUtils.parse(parentPath);

		const parentTree = ctx.getState().tree[parentPath];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem) {
			return;
		}

		ctx.setState(
			patch({
				tree: iif(
					!!ctx.getState().tree[grandparentPath],
					patch({
						[grandparentPath]: updateItem(
							(item) => item.name === parentName,
							patch({ hasChildren: parentTree.length - 1 > 0, isCollapsed: parentTree.length - 1 > 0 })
						),
						[parentPath]: removeItem((item) => item.name === name),
					}),
					patch({
						[parentPath]: removeItem((item) => item.name === name),
					})
				),
			})
		);
	}

	@Action(DirectoryTreeActions.Collapse)
	public collapse(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Collapse) {
		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || treeItem.isCollapsed) {
			return;
		}

		ctx.setState(
			patch({
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isCollapsed: true })),
				}),
			})
		);
	}

	@Action(DirectoryTreeActions.Expand)
	public expand(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Expand) {
		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || !treeItem.isCollapsed) {
			return;
		}

		ctx.setState(
			patch({
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isCollapsed: false })),
				}),
			})
		);
	}

	@Action(DirectoryTreeActions.Open)
	public open(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Open) {
		ctx.dispatch(new Navigate(['tree', ...action.path.split('/').filter((part) => part !== '')]));
	}

	@Action(DirectoryTreeActions.Select)
	public select(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Select) {
		const lastSelectedPath = ctx.getState().lastSelectedPath;

		if (lastSelectedPath) {
			ctx.dispatch(new DirectoryTreeActions.Unselect(lastSelectedPath));
		}

		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || treeItem.isSelected) {
			return;
		}

		ctx.setState(
			patch({
				lastSelectedPath: action.path,
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isSelected: true, isCollapsed: !treeItem.hasChildren })),
				}),
			})
		);
	}

	@Action(DirectoryTreeActions.Unselect)
	public unselect(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Unselect) {
		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || !treeItem.isSelected) {
			return;
		}

		ctx.setState(
			patch({
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isSelected: false })),
				}),
			})
		);
	}
}

