import { Injectable } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Store } from '@ngxs/store';
import { Observable } from 'rxjs';
import {
	ListItemDeleteEvent,
	ListItemOpenEvent,
	ListItemSelectEvent,
	ListItemUnselectEvent,
} from 'src/app/features/content-list/components/pure-content-list/components/selectable-directory-list-item/selectable-directory-list-item.component';
import { ContentType } from 'src/app/features/content-list/components/pure-content-list/pure-content-list.component';
import { ContentListActions } from 'src/app/features/content-list/state/content-list.actions';
import { ContentListState } from 'src/app/features/content-list/state/content-list.state';

@Injectable({
	providedIn: 'root',
})
export class ContentListService {
	private readonly store: Store;

	constructor(store: Store) {
		this.store = store;
	}

	public fetchContent(route: ActivatedRoute): Observable<void> {
		const path = decodeURI(route.snapshot.url.join('/'));

		return this.store.dispatch(new ContentListActions.FetchContent(path));
	}

	public selectContent(): Observable<Array<ContentType>> {
		return this.store.select(ContentListState.selectContent);
	}

	public selectIsInCreateMode(): Observable<boolean> {
		return this.store.select(ContentListState.selectIsInCreateMode);
	}

	public isAtLeastOneSelected(): Observable<boolean> {
		return this.store.select(ContentListState.isAtLeastOneSelected);
	}

	public select(event: ListItemSelectEvent) {
		if (event.shift) {
			this.store.dispatch(new ContentListActions.ShiftSelect(event.id));
		} else if (event.ctrl) {
			this.store.dispatch(new ContentListActions.CtrlSelect(event.id));
		} else {
			this.store.dispatch(new ContentListActions.SelectSingle(event.id));
		}
	}

	public unselect(event: ListItemUnselectEvent) {
		if (event.shift) {
			this.store.dispatch(new ContentListActions.ShiftUnselect(event.id));
		} else if (event.ctrl) {
			this.store.dispatch(new ContentListActions.CtrlUnselect(event.id));
		} else {
			this.store.dispatch(new ContentListActions.UnselectSingle(event.id));
		}
	}

	public open(event: ListItemOpenEvent, route: ActivatedRoute) {
		this.store.dispatch(new ContentListActions.Open(event.id, route));
	}

	public selectAll() {
		this.store.dispatch(new ContentListActions.SelectAll());
	}

	public unselectAll() {
		this.store.dispatch(new ContentListActions.UnselectAll());
	}

	public delete(event: ListItemDeleteEvent, route: ActivatedRoute) {
		this.store.dispatch(new ContentListActions.Delete(event.id, route));
	}

	public createDirectory(name: string, route: ActivatedRoute) {
		this.store.dispatch(new ContentListActions.CreateDirectory(name, route));
	}

	public displayDirectoryCreationWidget() {
		this.store.dispatch(new ContentListActions.DisplayDirectoryCreationWidget());
	}

	public cancelCreation() {
		this.store.dispatch(new ContentListActions.CancelCreation());
	}
}

import { HttpStatusCode } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Navigate } from '@ngxs/router-plugin';
import { Action, Selector, State, StateContext } from '@ngxs/store';
import { append, patch, removeItem, updateItem } from '@ngxs/store/operators';
import { DirectoryService, FileService } from 'generated';
import { filter, switchMap, tap } from 'rxjs';
import { ContentType, Directory, File, Type } from 'src/app/features/content-list/components/pure-content-list/pure-content-list.component';
import { ContentListActions } from 'src/app/features/content-list/state/content-list.actions';
import { DirectoryTreeActions } from 'src/app/features/directory-tree/state/directory-tree.actions';

export interface ContentListStateModel {
	items: ContentType[];
	lastInteractedId: number | undefined;
	isInCreateMode: boolean;
}

@State<ContentListStateModel>({
	name: 'content_list',
	defaults: {
		lastInteractedId: undefined,
		isInCreateMode: false,
		items: [],
	},
})
@Injectable()
export class ContentListState {
	private directoryService: DirectoryService;

	private fileService: FileService;

	constructor(directoryService: DirectoryService, fileService: FileService) {
		this.directoryService = directoryService;
		this.fileService = fileService;
	}

	@Selector()
	public static selectContent(state: ContentListStateModel) {
		return state.items;
	}

	@Selector()
	public static selectIsInCreateMode(state: ContentListStateModel) {
		return state.isInCreateMode;
	}

	@Selector()
	public static isAtLeastOneSelected(state: ContentListStateModel) {
		return state.items.some((item) => item.isSelected);
	}

	@Action(ContentListActions.CancelCreation)
	public cancelCreation(ctx: StateContext<ContentListStateModel>) {
		ctx.setState(
			patch({
				isInCreateMode: false,
			})
		);
	}

	@Action(ContentListActions.DisplayDirectoryCreationWidget)
	public displayDirectoryCreationWidget(ctx: StateContext<ContentListStateModel>) {
		ctx.setState(
			patch({
				isInCreateMode: true,
			})
		);
	}

	@Action(ContentListActions.CreateDirectory)
	public createDirectory(ctx: StateContext<ContentListStateModel>, action: ContentListActions.CreateDirectory) {
		const path = decodeURI('/' + action.route.snapshot.url.join('/') + '/' + action.name + '/');

		return this.directoryService.createDirectory(path, 'response').pipe(
			filter((response) => response.status === HttpStatusCode.Created),
			switchMap(() => this.directoryService.getDirectoryMetadata(path)),
			tap((metadata) => {
				ctx.setState(
					patch({
						items: append([
							{
								...metadata,
								isSelected: false,
								isHighlighted: true,
								isBeingProcessed: false,
								id: ctx.getState().items.length,
								type: Type.Directory,
							},
						]),
						isInCreateMode: false,
					})
				);
			})
		);
	}

	@Action(ContentListActions.Delete)
	public delete(ctx: StateContext<ContentListStateModel>, action: ContentListActions.Delete) {
		const name = ctx.getState().items.find((item) => item.id === action.id)?.name;

		if (!name) {
			return;
		}

		ctx.setState(
			patch({
				items: updateItem((item) => item.id === action.id, patch({ isBeingProcessed: true })),
			})
		);

		const path = decodeURI('/' + action.route.snapshot.url.join('/') + '/' + name + '/');

		return this.directoryService.deleteDirectory(path, 'response').pipe(
			tap((response) => {
				if (response.status === HttpStatusCode.NoContent) {
					ctx.setState(
						patch({
							items: removeItem((item) => item.id === action.id),
						})
					);

					ctx.dispatch(new DirectoryTreeActions.Remove(path));
				}
			})
		);
	}

	// @Action(ContentListActions.DeleteSelected)
	// public deleteSelected(ctx: StateContext<ContentListStateModel>, action: ContentListActions.DeleteSelected) {
	// 	const selected = ctx.getState().items.filter((item) => item.isSelected);

	// 	ctx.setState(
	// 		patch({
	// 			items: updateItem((item) => selected.map((item) => item.id).includes(item.id), patch({ isBeingLoaded: true })),
	// 		})
	// 	);

	// 	const observables = selected.map((item) => {
	// 		const path = decodeURI(action.route.snapshot.url.join('/') + '/' + item.name);
	// 		if (item.type === Type.Directory) {
	// 			return { id: item.id, path: path, observable: this.directoryService.deleteDirectory(path, 'response') };
	// 		} else {
	// 			return { id: item.id, path: path, observable: this.fileService.deleteFile(path, 'response') };
	// 		}
	// 	});

	// 	return forkJoin(
	// 		observables.map((item) =>
	// 			item.observable.pipe(
	// 				map((response) => ({
	// 					id: item.id,
	// 					path: item.path,
	// 					response,
	// 				}))
	// 			)
	// 		)
	// 	).pipe(
	// 		delay(5000),
	// 		tap((results) => {
	// 			for (const result of results) {
	// 				if (result.response.status === HttpStatusCode.NoContent) {
	// 					ctx.setState(
	// 						patch({
	// 							items: removeItem((item) => item.id === result.id),
	// 						})
	// 					);

	// 					ctx.dispatch(new DirectoryTreeActions.Remove(result.path));
	// 				}
	// 			}
	// 		})
	// 	);
	// }

	@Action(ContentListActions.FetchContent)
	public fetchContent(ctx: StateContext<ContentListStateModel>, action: ContentListActions.FetchContent) {
		return this.directoryService.getDirectoryContent(action.path).pipe(
			tap((content) => {
				ctx.setState(
					patch({
						items: [
							...(content.directories.map((directory) => ({ isSelected: false, type: Type.Directory, ...directory })) as Omit<Directory, 'id'>[]),
							...(content.files.map((file) => ({ isSelected: false, type: Type.File, ...file })) as Omit<File, 'id'>[]),
						].map((element, index) => ({ id: index, ...element })),
						lastInteractedId: undefined,
					})
				);
			})
		);
	}

	@Action(ContentListActions.Open)
	public open(ctx: StateContext<ContentListStateModel>, action: ContentListActions.Open) {
		const name = ctx.getState().items.find((item) => item.id === action.id)?.name;

		if (!name) {
			return;
		}

		ctx.dispatch(new Navigate([name], {}, { relativeTo: action.route }));
	}

	@Action(ContentListActions.SelectSingle)
	public selectItem(ctx: StateContext<ContentListStateModel>, action: ContentListActions.SelectSingle) {
		ctx.setState(
			patch({
				items: updateItem((item) => item.id === action.id, patch({ isSelected: true })),
				lastInteractedId: action.id,
			})
		);
	}

	@Action(ContentListActions.UnselectSingle)
	public unselectItem(ctx: StateContext<ContentListStateModel>, action: ContentListActions.UnselectSingle) {
		ctx.setState(
			patch({
				items: updateItem((item) => item.id === action.id, patch({ isSelected: false })),
				lastInteractedId: action.id,
			})
		);
	}

	@Action([ContentListActions.ShiftSelect, ContentListActions.CtrlSelect])
	public rangeSelect(ctx: StateContext<ContentListStateModel>, action: ContentListActions.ShiftSelect | ContentListActions.CtrlSelect) {
		const lastInteractedId = ctx.getState().lastInteractedId;

		if (lastInteractedId === undefined) {
			ctx.dispatch(new ContentListActions.SelectSingle(action.id));
			return;
		}

		const start = Math.min(lastInteractedId, action.id);
		const end = Math.max(lastInteractedId, action.id);

		if (end - start <= 2 || this.isUnselectedInRange(ctx, start, end)) {
			this.selectRange(ctx, start, end);
		} else {
			this.unselectRange(ctx, start, end);
		}
	}

	@Action([ContentListActions.ShiftUnselect, ContentListActions.CtrlUnselect])
	public rangeUnselect(ctx: StateContext<ContentListStateModel>, action: ContentListActions.ShiftUnselect | ContentListActions.CtrlUnselect) {
		const lastInteractedId = ctx.getState().lastInteractedId;

		if (lastInteractedId === undefined) {
			ctx.dispatch(new ContentListActions.SelectSingle(action.id));
			return;
		}

		const start = Math.min(lastInteractedId, action.id);
		const end = Math.max(lastInteractedId, action.id);

		if (this.isUnselectedInRange(ctx, start, end)) {
			this.selectRange(ctx, start, end);
		} else {
			this.unselectRange(ctx, start === action.id ? start + 1 : start, end);
		}

		ctx.dispatch(new ContentListActions.SelectSingle(action.id));
	}

	@Action(ContentListActions.SelectAll)
	public selectAll(ctx: StateContext<ContentListStateModel>) {
		const items = ctx.getState().items;

		ctx.setState(
			patch({
				items: items.map((item) => ({ ...item, isSelected: true })),
				lastInteractedId: items.at(-1)?.id,
			})
		);
	}

	@Action(ContentListActions.UnselectAll)
	public unselectAll(ctx: StateContext<ContentListStateModel>) {
		const items = ctx.getState().items;

		ctx.setState(
			patch({
				items: items.map((item) => ({ ...item, isSelected: false })),
				lastInteractedId: items.at(0)?.id,
			})
		);
	}

	private selectRange(ctx: StateContext<ContentListStateModel>, start: number, end: number) {
		for (let i = start; i <= end; i++) {
			ctx.dispatch(new ContentListActions.SelectSingle(i));
		}
	}

	private unselectRange(ctx: StateContext<ContentListStateModel>, start: number, end: number) {
		for (let i = start; i <= end; i++) {
			ctx.dispatch(new ContentListActions.UnselectSingle(i));
		}
	}

	private isUnselectedInRange(ctx: StateContext<ContentListStateModel>, from: number, to: number): boolean {
		if (from > to) {
			[from, to] = [to, from];
		}

		return ctx.getState().items.find((item) => item.id >= from && item.id <= to && !item.isSelected) !== undefined;
	}
}

import { Injectable } from '@angular/core';
import { Navigate } from '@ngxs/router-plugin';
import { Action, State, StateContext } from '@ngxs/store';
import { iif, patch, removeItem, updateItem } from '@ngxs/store/operators';
import { DirectoryService } from 'generated';
import { forkJoin, map, mergeMap, tap } from 'rxjs';
import { DirectoryTreeActions } from 'src/app/features/directory-tree/state/directory-tree.actions';
import { PathUtils } from 'src/app/utils/PathUtils';

export const DirectoryTreeStateToken = 'directory_tree';

export interface DirectoryTreeStateModel {
	lastSelectedPath: string | undefined;
	root: Node;
	tree: TreeModel;
}

export interface TreeModel {
	[path: string]: Array<Node>;
}

export interface Node {
	id: number;
	name: string;
	path: string;
	isCollapsed: boolean;
	hasChildren: boolean;
	isSelected: boolean;
}

@State<DirectoryTreeStateModel>({
	name: DirectoryTreeStateToken,
	defaults: {
		lastSelectedPath: undefined,
		tree: {},
		root: {
			id: 0,
			name: 'root',
			path: '/root/',
			isCollapsed: false,
			isSelected: false,
			hasChildren: true,
		},
	},
})
@Injectable()
export class DirectoryTreeState {
	private directoryService: DirectoryService;

	private id: number = 0;

	constructor(directoryService: DirectoryService) {
		this.directoryService = directoryService;
	}

	@Action(DirectoryTreeActions.FetchContent)
	public fetchContent(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.FetchContent) {
		if (PathUtils.ensureSlashes(action.path) in ctx.getState().tree) {
			return;
		}

		return this.directoryService.getDirectoryContent(action.path).pipe(
			mergeMap((content) => {
				const observables = content.directories.map((directory) =>
					this.directoryService.getDirectoryMetadata((action.path.endsWith('/') ? action.path : action.path + '/') + directory.name).pipe(
						map((metadata) => ({
							id: ++this.id,
							name: metadata.name,
							path: metadata.path,
							isCollapsed: true,
							isSelected: false,
							hasChildren: metadata.directories > 0,
						}))
					)
				);

				return forkJoin(observables);
			}),
			tap((children) => {
				const tree = ctx.getState().tree;

				ctx.setState(
					patch({
						tree: {
							...tree,
							[PathUtils.ensureSlashes(action.path)]: children,
						},
					})
				);
			})
		);
	}

	@Action(DirectoryTreeActions.Remove)
	public remove(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Remove) {
		const { name, dir: parentPath } = PathUtils.parse(action.path);
		const { name: parentName, dir: grandparentPath } = PathUtils.parse(parentPath);

		const parentTree = ctx.getState().tree[parentPath];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem) {
			return;
		}

		ctx.setState(
			patch({
				tree: iif(
					!!ctx.getState().tree[grandparentPath],
					patch({
						[grandparentPath]: updateItem(
							(item) => item.name === parentName,
							patch({ hasChildren: parentTree.length - 1 > 0, isCollapsed: parentTree.length - 1 > 0 })
						),
						[parentPath]: removeItem((item) => item.name === name),
					}),
					patch({
						[parentPath]: removeItem((item) => item.name === name),
					})
				),
			})
		);
	}

	@Action(DirectoryTreeActions.Collapse)
	public collapse(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Collapse) {
		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || treeItem.isCollapsed) {
			return;
		}

		ctx.setState(
			patch({
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isCollapsed: true })),
				}),
			})
		);
	}

	@Action(DirectoryTreeActions.Expand)
	public expand(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Expand) {
		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || !treeItem.isCollapsed) {
			return;
		}

		ctx.setState(
			patch({
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isCollapsed: false })),
				}),
			})
		);
	}

	@Action(DirectoryTreeActions.Open)
	public open(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Open) {
		ctx.dispatch(new Navigate(['tree', ...action.path.split('/').filter((part) => part !== '')]));
	}

	@Action(DirectoryTreeActions.Select)
	public select(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Select) {
		const lastSelectedPath = ctx.getState().lastSelectedPath;

		if (lastSelectedPath) {
			ctx.dispatch(new DirectoryTreeActions.Unselect(lastSelectedPath));
		}

		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || treeItem.isSelected) {
			return;
		}

		ctx.setState(
			patch({
				lastSelectedPath: action.path,
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isSelected: true, isCollapsed: !treeItem.hasChildren })),
				}),
			})
		);
	}

	@Action(DirectoryTreeActions.Unselect)
	public unselect(ctx: StateContext<DirectoryTreeStateModel>, action: DirectoryTreeActions.Unselect) {
		const { name, dir: parent } = PathUtils.parse(action.path);

		const parentTree = ctx.getState().tree[parent];
		const treeItem = parentTree?.find((item) => item.name === name);

		if (!treeItem || !treeItem.isSelected) {
			return;
		}

		ctx.setState(
			patch({
				tree: patch({
					[parent]: updateItem((item) => item.name === name, patch({ isSelected: false })),
				}),
			})
		);
	}
}

