import { Component, ElementRef, EventEmitter, HostBinding, Input, Output, ViewChild } from '@angular/core';

export type CheckboxCheckEvent = {
	ctrl: boolean;
	shift: boolean;
	alt: boolean;
	nativeEvent: MouseEvent | PointerEvent;
};

export type CheckboxUncheckEvent = {
	ctrl: boolean;
	shift: boolean;
	alt: boolean;
	nativeEvent: MouseEvent | PointerEvent;
};

@Component({
	selector: 'checkbox',
	standalone: true,
	templateUrl: './checkbox.component.html',
	styleUrl: './checkbox.component.css',
})
export class CheckboxComponent {
	@ViewChild('input')
	public input!: ElementRef<HTMLInputElement>;

	@Output()
	public onCheck: EventEmitter<CheckboxCheckEvent> = new EventEmitter();

	@Output()
	public onUncheck: EventEmitter<CheckboxUncheckEvent> = new EventEmitter();

	@HostBinding('class.selected')
	@Input('isSelected')
	public isSelected: boolean = false;

	public onClick(event: PointerEvent | MouseEvent) {
		event.preventDefault();

		// this.isSelected = !this.isSelected;

		const payload: CheckboxCheckEvent | CheckboxUncheckEvent = { nativeEvent: event, ctrl: event.ctrlKey, shift: event.shiftKey, alt: event.altKey };

		if (!this.isSelected) {
			this.onCheck.emit(payload);
		} else {
			this.onUncheck.emit(payload);
		}
	}
}

import { Component } from '@angular/core';
import { ActivatedRoute, NavigationEnd, Router } from '@angular/router';
import { filter, Observable, Subscription } from 'rxjs';
import {
	ListItemDeleteEvent,
	ListItemOpenEvent,
	ListItemSelectEvent,
	ListItemUnselectEvent,
} from 'src/app/features/content-list/components/pure-content-list/components/selectable-directory-list-item/selectable-directory-list-item.component';
import { ContentType, PureContentListComponent } from 'src/app/features/content-list/components/pure-content-list/pure-content-list.component';
import { ContentListService } from 'src/app/features/content-list/content-list.service';

@Component({
	selector: 'content-list',
	standalone: true,
	templateUrl: './content-list.component.html',
	styleUrl: './content-list.component.css',
	imports: [PureContentListComponent],
})
export class ContentListComponent {
	private readonly service: ContentListService;

	private readonly router: Router;

	private readonly route: ActivatedRoute;

	private readonly content$: Observable<Array<ContentType>>;

	private readonly isInSelectMode$: Observable<boolean>;

	private readonly isInCreateMode$: Observable<boolean>;

	private readonly subscriptions: Subscription = new Subscription();

	public isInCreateMode: boolean = false;

	public isInSelectMode: boolean = false;

	public content: Array<ContentType> = [];

	constructor(service: ContentListService, route: ActivatedRoute, router: Router) {
		this.service = service;
		this.route = route;
		this.router = router;

		this.isInCreateMode$ = service.selectIsInCreateMode();
		this.content$ = service.selectContent();
		this.isInSelectMode$ = service.isAtLeastOneSelected();
	}

	ngOnInit() {
		this.service.fetchContent(this.route);

		this.subscriptions.add(
			this.router.events
				.pipe(filter((event): event is NavigationEnd => event instanceof NavigationEnd))
				.subscribe(() => this.service.fetchContent(this.route))
		);

		this.subscriptions.add(
			this.content$.subscribe((content) => {
				this.content = content;
			})
		);

		this.subscriptions.add(
			this.isInSelectMode$.subscribe((isInSelectMode) => {
				this.isInSelectMode = isInSelectMode;
			})
		);

		this.subscriptions.add(
			this.isInCreateMode$.subscribe((isInCreateMode) => {
				this.isInCreateMode = isInCreateMode;
			})
		);
	}

	ngOnDestroy() {
		this.subscriptions.unsubscribe();
	}

	onSelect(event: ListItemSelectEvent) {
		this.service.select(event);
	}

	onUnselect(event: ListItemUnselectEvent) {
		this.service.unselect(event);
	}

	onOpen(event: ListItemOpenEvent) {
		this.service.open(event, this.route);
	}

	selectAll() {
		this.service.selectAll();
	}

	unselectAll() {
		this.service.unselectAll();
	}

	onDelete(event: ListItemDeleteEvent) {
		const confirmation = confirm(`Sicher, dass du ${this.content.find((x) => x.id === event.id)?.name} lschen willst?`);

		if (confirmation) {
			this.service.delete(event, this.route);
		}
	}

	onPlusClick() {
		this.service.displayDirectoryCreationWidget();
	}

	onCreate(name: string) {
		this.service.createDirectory(name, this.route);
	}

	onCancelCreation() {
		this.service.cancelCreation();
	}
}

import { Component, EventEmitter, HostBinding, Input, Output } from '@angular/core';
