	templateUrl: './directory-list-item.component.html',
	styleUrl: './directory-list-item.component.css',
	imports: [LoadingSpinnerComponent],
})
export class DirectoryListItemComponent implements ISelectable, IDownloadable, IRenamable, IDeletable {
	@Input({ required: true })
	public metadata!: DirectoryContentDirectory;

	@HostBinding('class.selected')
	@Input('isSelected')
	public isSelected: boolean = false;

	@HostBinding('class.highlighted')
	@Input('isHighlighted')
	public isHighlighted: boolean = false;

	@HostBinding('class.is-being-processed')
	@Input()
	public isBeingProcessed: boolean = false;

	@Output()
	public onRename: EventEmitter<void> = new EventEmitter();

	@Output()
	public onDelete: EventEmitter<void> = new EventEmitter();

	@Output()
	public onDownload: EventEmitter<void> = new EventEmitter();

	select(): void {
		this.isSelected = true;
	}

	unselect(): void {
		this.isSelected = false;
	}

	public rename(event?: Event): void {
		event?.preventDefault();

		this.onRename.emit();
	}

	public delete(event?: Event): void {
		event?.preventDefault();

		this.onDelete.emit();
	}

	public download(event?: Event): void {
		event?.preventDefault();

		this.onDownload.emit();
	}

	protected formatBytes(bytes: number, decimals: number = 2): string {
		if (!+bytes) return '0 B';

		const k = 1024;
		const dm = decimals < 0 ? 0 : decimals;
		const sizes = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];

		const i = Math.floor(Math.log(bytes) / Math.log(k));

		return `${parseFloat((bytes / Math.pow(k, i)).toFixed(dm))} ${sizes[i]}`;
	}

	protected formatDate(dateOrString: Date | string): string {
		const date = dateOrString instanceof Date ? dateOrString : new Date(Date.parse(dateOrString));

		const day = date.toLocaleDateString('de-DE', { day: 'numeric' });
		const month = date.toLocaleDateString('de-DE', { month: 'short' });
		const year = date.toLocaleDateString('de-DE', { year: '2-digit' });

		return `${day} ${month} ${year}`;
	}
}

import { Component } from '@angular/core';
import { ActivatedRoute, NavigationEnd, Router } from '@angular/router';
import { Store } from '@ngxs/store';
import { filter, Observable } from 'rxjs';
import { PureDirectoryTreeComponent } from 'src/app/features/directory-tree/components/pure-directory-tree/pure-directory-tree.component';
import { DirectoryTreeActions } from 'src/app/features/directory-tree/state/directory-tree.actions';
import { Node } from 'src/app/features/directory-tree/state/directory-tree.state';

@Component({
	standalone: true,
	selector: 'directory-tree',
	styleUrl: './directory-tree.component.css',
	templateUrl: './directory-tree.component.html',
	imports: [PureDirectoryTreeComponent],
})
export class DirectoryTreeComponent {
	private readonly store: Store;

	private readonly router: Router;

	private readonly route: ActivatedRoute;

	public root!: Node;

	public tree!: {
		[path: string]: Array<Node>;
	};

	public root$: Observable<Node>;

	public tree$: Observable<{
		[path: string]: Array<Node>;
	}>;

	public constructor(store: Store, router: Router, route: ActivatedRoute) {
		this.store = store;
		this.route = route;
		this.router = router;

		this.root$ = store.select((state) => state['directory_tree'].root);
		this.tree$ = store.select((state) => state['directory_tree'].tree);
	}

	ngOnInit() {
		this.router.events.pipe(filter((event): event is NavigationEnd => event instanceof NavigationEnd)).subscribe(() => {
			const path = '/' + decodeURI(this.route.snapshot.url.join('/'));
			this.store.dispatch([new DirectoryTreeActions.FetchContent(path), new DirectoryTreeActions.Select(path)]);
		});

		this.root$.subscribe((root) => (this.root = root));
		this.tree$.subscribe((tree) => (this.tree = tree));

		this.store.dispatch([
			new DirectoryTreeActions.FetchContent(this.route.snapshot.url.join('/')),
			new DirectoryTreeActions.Select(this.route.snapshot.url.join('/')),
		]);
	}

	public onExpand(path: string) {
		this.store.dispatch(new DirectoryTreeActions.Expand(path));
	}

	public onCollapse(path: string) {
		this.store.dispatch(new DirectoryTreeActions.Collapse(path));
	}

	public onLoadContent(path: string) {
		this.store.dispatch(new DirectoryTreeActions.FetchContent(path));
	}

	public onSelect(path: string) {
		this.store.dispatch(new DirectoryTreeActions.Open(path));
	}
}

import { Component, EventEmitter, HostBinding, HostListener, Input, Output } from '@angular/core';
import { Node } from 'src/app/features/directory-tree/state/directory-tree.state';
import { LoadingSpinnerComponent, Thickness } from 'src/app/shared/components/loading-spinner/loading-spinner.component';

@Component({
	selector: 'directory-tree-item',
	standalone: true,
	templateUrl: './directory-tree-item.component.html',
	styleUrl: './directory-tree-item.component.css',
	imports: [LoadingSpinnerComponent],
})
export class DirectoryTreeItemComponent {
	@Input({ required: true })
	public node!: Node;

	@Input({ required: true })
	public tree!: {
		[path: string]: Array<Node>;
	};

	@Input()
	public isRoot: boolean = false;

	@HostBinding('class.is-collapsed')
	public isCollapsed!: boolean;

	@HostBinding('class.has-children')
	public hasChildren!: boolean;

	@HostBinding('class.is-loading')
	public isLoading!: boolean;

	@HostBinding('class.is-selected')
	public isSelected!: boolean;

	@Output()
	public onLoadContent: EventEmitter<string> = new EventEmitter();

	@Output()
	public onCollapse: EventEmitter<string> = new EventEmitter();

	@Output()
	public onExpand: EventEmitter<string> = new EventEmitter();

	@Output()
	public onSelect: EventEmitter<string> = new EventEmitter();

	public loadingSpinnerThickness = Thickness.Thick;

	ngOnInit() {
		this.isCollapsed = this.node.isCollapsed;
		this.hasChildren = this.node.hasChildren;
		this.isSelected = this.node.isSelected;

		this.isLoading = this.hasChildren && !this.tree[this.node.path];
	}

	ngOnChanges() {
		this.isCollapsed = this.node.isCollapsed;
		this.hasChildren = this.node.hasChildren;
		this.isSelected = this.node.isSelected;

		this.isLoading = this.hasChildren && !this.tree[this.node.path];
	}

	public onArrowClick(event: MouseEvent | PointerEvent) {
		event.stopPropagation();

		if (this.isLoading) {
			this.onLoadContent.emit(this.node.path);
		}

		if (this.isCollapsed) {
			this.onExpand.emit(this.node.path);
		} else {
			this.onCollapse.emit(this.node.path);
		}
	}

	@HostListener('click', ['$event'])
	private onClick(event: MouseEvent | PointerEvent) {
		if (this.isRoot) {
			return;
		}

		event.stopPropagation();

		this.onSelect.emit(this.node.path);
	}
}

