export enum Type {
	Minimalistic = 'minimalistic',
	Rounded = 'rounded',
}

export enum Thickness {
	UltraThick = 'ultra-thick',
	Thick = 'thick',
	Regular = 'regular',
	Thin = 'thin',
}

const ThicknessMap: Record<Thickness, number> = {
	[Thickness.UltraThick]: 0.24,
	[Thickness.Thick]: 0.18,
	[Thickness.Regular]: 0.09,
	[Thickness.Thin]: 0.045,
};

@Component({
	selector: 'loading-spinner',
	standalone: true,
	templateUrl: './loading-spinner.component.html',
	styleUrl: './loading-spinner.component.css',
})
export class LoadingSpinnerComponent {
	/**
	 * The thickness of the spinner.
	 * @type {Thickness}
	 */
	@Input()
	public thickness!: Thickness;

	/**
	 * The type of the spinner.
	 * @type {Type}
	 */
	@Input()
	public type!: Type;

	/**
	 * Reference to the host element.
	 * @type {ElementRef<HTMLDivElement>}
	 */
	private readonly hostRef: ElementRef<HTMLDivElement>;

	/**
	 * Creates a new LoadingSpinnerComponent instance.
	 * @constructor
	 *
	 * @param   {ElementRef}              hostRef the entityManager
	 * @returns {LoadingSpinnerComponent}         the LoadingSpinnerComponent instance
	 */
	public constructor(hostRef: ElementRef) {
		this.hostRef = hostRef;
	}

	public apply() {
		this.applyDefaults();
		this.applyThickness();
	}

	public ngAfterContentInit() {
		this.apply();

		new ResizeObserver(() => this.applyThickness()).observe(this.hostRef.nativeElement);
	}

	public ngOnChanges() {
		this.apply();
	}

	@HostBinding('class')
	get classList(): string {
		return `${this.thickness.toString()} ${this.type.toString()}`;
	}

	@HostListener('window:resize')
	public onResize() {
		this.applyThickness();
	}

	private applyDefaults() {
		this.thickness = this.thickness || Thickness.Thick;
		this.type = this.type || Type.Rounded;
	}

	/**
	 * Calculate the thickness of the spinner based off its computed width.
	 *
	 * @param   {number} width the width of the component
	 * @returns {number}       the calculated thickness
	 */
	private calculateThickness(width: number) {
		return Math.round(width * ThicknessMap[this.thickness]);
	}

	/**
	 * Calculate the thickness, format for CSS and assign to host binding.
	 */
	private applyThickness() {
		let calculatedThickness = `${this.calculateThickness(this.hostRef.nativeElement.clientWidth)}px`;

		this.hostRef.nativeElement.style.setProperty('--thickness', calculatedThickness);
	}
}

import { Component, ElementRef, EventEmitter, HostBinding, HostListener, Output, ViewChild } from '@angular/core';

@Component({
	standalone: true,
	selector: 'nameable-directory-item',
	templateUrl: './nameable-directory-item.component.html',
	styleUrl: './nameable-directory-item.component.css',
})
export class NameableDirectoryItemComponent {
	private readonly regExp =
		/^([-_.]?[a-zA-Z-0-9\u00c4\u00e4\u00d6\u00f6\u00dc\u00fc\u00df])([-_. ]?[a-zA-Z-0-9\u00c4\u00e4\u00d6\u00f6\u00dc\u00fc\u00df])*$/m;

	private readonly ref: ElementRef;

	@ViewChild('input')
	public input!: ElementRef<HTMLInputElement>;

	@HostBinding('class.is-invalid')
	public isNameInvalid: boolean = false;

	@Output()
	public onSuccess: EventEmitter<string> = new EventEmitter();

	@Output()
	public onDestroy: EventEmitter<string> = new EventEmitter();

	public constructor(ref: ElementRef) {
		this.ref = ref;
	}

	ngAfterViewInit() {
		this.focus();

		this.input.nativeElement.addEventListener('input', () => (this.isNameInvalid = !this.isNameValid()));

		this.input.nativeElement.addEventListener('change', () => this.onSubmit());
	}

	@HostListener('document:click', ['$event'])
	public clickOut(event: Event) {
		if (!this.ref.nativeElement.contains(event.target)) {
			if (this.input.nativeElement.value === '') {
				return this.onDestroy.emit();
			}

			this.onSubmit();
		}
	}

	private onSubmit() {
		if (!this.isNameValid()) {
			return;
		}

		this.onSuccess.emit(this.input.nativeElement.value);
	}

	private isNameValid() {
		return this.regExp.test(this.input.nativeElement.value);
	}

	private focus() {
		this.input.nativeElement.focus();
	}
}

